class orario:
    def __init__(self, ora, minuti):
        self.ora = ora
        self.minuti = minuti
    def get_ora(self):
        return self.ora
    def get_minuti(self):
        return self.minuti
    def set_ora(self, ora):
        self.ora = ora
        return self.ora
    def set_minuti(self, minuti):
        self.minuti = minuti
        return self.minuti
    def get_orario(self):
        return self.ora, self.minuti
    def set_orario(self, ora, minuti):
        self.ora = ora
        self.minuti = minuti
        return self.ora, self.minuti
    def fusorario(self, paese: Paese):
        return self.ora + paese.get_fuso()
